import axios from "axios";
import React, { useState, useEffect, Fragment } from "react";
import { Link } from "react-router-dom";

const QuestionDetail = (props) => {
	const [question, setQuestion] = useState({});

	useEffect(() => {
		const slug = props.match.params.pk;

		const fetchData = async () => {
			try {
				const res = await axios.get(
					`${process.env.REACT_APP_API_URL}/question/${slug}`
				);
				setQuestion(res.data);
			} catch (error) {}
		};
		fetchData();
	}, [props.match.params.pk]);

    return (
        <Fragment>
            <div className="main-div">
                <img src={logo} className="nss-logo" />
                <p className="logo-text">NSS IITR Presents</p>
                <p className="heading" >SURPRISE SITUATION TRIVIA !!</p>
                <p className="sub-heading" >Select a Number</p>
                <div className="container">{getQuestions()}</div>
            </div>
				<button
					class="button button1"
					style={{
						backgroundColor: "whitesmoke",
						border: 0,
						color: "white",
						padding: 8,
						textAlign: "center",
						textDecoration: "none",
						margin: 4,
						cursor: "pointer",
						marginLeft: 4,
					}}
				>
					<Link
						to="/questions/"
						className="font-weight-bold"
						style={{
							textDecoration: "none",
							color: "black",
							fontFamily: "Galdeano, sans-serif",
							fontSize: 18,
						}}
					>
						Back to Questions
					</Link>
            </button>
        </Fragment>
	);
};
export default QuestionDetail;
