import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import '../static/css/question_list_page.css';

const QuestionList = () => {
	const [questions, setQuestions] = useState([]);

	useEffect(() => {
		const fetchQuestions = async () => {
			try {
				const res = await axios.get(`${process.env.REACT_APP_API_URL}/questions/`);
				setQuestions(res.data);
			} catch (error) {}
		};

		fetchQuestions();
	}, []);

	const getQuestions = () => {
        let list = [];
        let result = [];

		questions.map((question) => {
            return list.push(
                <div
					className="row g-0 rounded overflow-hidden flex-md-row mb-0  h-md position-relative"
				>
                    <Link
                        to={`/question/${question.id}`}
                        style={{
                            textDecoration: "none",
                            fontFamily: "Pacifico, cursive",
                            }}>
                        <div className="question-number-box">
                            {question.ranking}
                        </div>
                    </Link>
                </div>
			);
		});

		for (let i = 0; i < list.length; i += 2) {
			result.push(
				<div key={i} className="row mb-0">
					<div className="col-md-6 p-4">{list[i]}</div>
					<div className="col-md-6 p-4">{list[i + 1] ? list[i + 1] : null}</div>
				</div>
			);
		}

		return result;
    };

    return (
        <center>
            <div>
                <h1 style={{ fontFamily: 'Tenali Ramakrishna, sans-serif' }} >SURPRISE SITUATION TRIVIA !!</h1>
                <div className="container mt-2">{getQuestions()}</div>
                <h1 style={{ fontFamily: 'Tenali Ramakrishna, sans-serif' }} >Select a Number</h1>
            </div>
        </center>
	);
};

export default QuestionList;
